import org.junit.Assert;
import org.junit.jupiter.api.Test;

import java.util.Arrays;
import java.util.HashSet;
import java.util.List;
import java.util.Set;

public class 모음찾기 {

    @Test
    public void test(){
        String a = "";

        String test = findSubstring(a, 100);
        Assert.assertEquals(test, "test");
    }

    private static Set<Character> set = new HashSet<>(Arrays.asList('a', 'e', 'i', 'o', 'u'));

    public static String findSubstring(String s, int k) {

        String result = "Not found!";

        int count = 0;
        for(int i=0; i<k; i++){
            if(set.contains(s.charAt(i)))  count++;
        }

        if(count > 0) result = s.substring(0, k);

        int maximum = count;
        int lowIndex = 0;
        int lastIndex = k-1;

        while(lastIndex<s.length()-1){
            if(set.contains(s.charAt(lowIndex))) count--;

            lowIndex++;
            lastIndex++;

            if(set.contains(s.charAt(lastIndex))) count++;

            if(maximum < count){
                maximum = count;
                result = s.substring(lowIndex, lastIndex+1);
            }
        }


        return result;
    }

}
